        return 0 <= r < len(board) and 0 <= c < len(board[0]) and not visited[r][c]

    def dfs(board, visited, r, c, steps, max_steps, max_start):
        directions = {'L': (0, -1), 'R': (0, 1), 'U': (-1, 0), 'D': (1, 0)}
        visited[r][c] = True

        for dr, dc in directions.values():
            new_r, new_c = r + dr, c + dc
            if is_valid_move(board, visited, new_r, new_c) and board[new_r][new_c] != '.':
                new_steps = steps + 1
                if new_steps > max_steps:
                    max_steps = new_steps
                    max_start = (r, c)
                    max_start, max_steps = dfs(board, visited, new_r, new_c, new_steps, max_steps, max_start)

        visited[r][c] = False
        return max_start, max_steps

    result = []
    for n, m, board in test_cases:
        visited = [[False for _ in range(m)] for _ in range(n)]
        max_steps = 0
        start_r, start_c = 0, 0

        for i in range(n):
            for j in range(m):
                if board[i][j] != '.':
                    (res_r, res_c), res_steps = dfs(board, visited, i, j, 0, 0, (0, 0))
                    if res_steps > max_steps:
                        max_steps = res_steps
                        start_r, start_c = res_r, res_c

        result.append((start_r + 1, start_c + 1, max_steps))

    return result
