def find_starting_cell(t: int, test_cases: List[Tuple[int, int, List[str]]]) -> List[Tuple[int, int, int]]:
    def get_max_steps(board: List[str]) -> int:
        n = len(board)
        m = len(board[0])
        max_steps = 0

        def dfs(r, c, steps):
            nonlocal max_steps
            if r < 0 or r >= n or c < 0 or c >= m or board[r][c] == '#':
                max_steps = max(max_steps, steps)
                return
            if board[r][c] == 'L':
                board[r] = board[r][:c] + '#' + board[r][c+1:]
                dfs(r, c-1, steps+1)
                board[r] = board[r][:c] + 'L' + board[r][c+1:]
            elif board[r][c] == 'R':
                board[r] = board[r][:c] + '#' + board[r][c+1:]
                dfs(r, c+1, steps+1)
                board[r] = board[r][:c] + 'R' + board[r][c+1:]
            elif board[r][c] == 'U':
                board[r] = board[r][:c] + '#' + board[r][c+1:]
                dfs(r-1, c, steps+1)
                board[r] = board[r][:c] + 'U' + board[r][c+1:]
            elif board[r][c] == 'D':
                board[r] = board[r][:c] + '#' + board[r][c+1:]
                dfs(r+1, c, steps+1)
                board[r] = board[r][:c] + 'D' + board[r][c+1:]

        for i in range(n):
            for j in range(m):
                if board[i][j] != '#':
                    dfs(i, j, 0)

        return max_steps

    result = []
    for n, m, board in test_cases:
        max_steps = get_max_steps(board)
        result.append((1, 1, max_steps))

    return result

