            visited[r][c] = True
            direction = board[r][c]
            if direction == 'L':
                c -= 1
            elif direction == 'R':
                c += 1
            elif direction == 'U':
                r -= 1
            elif direction == 'D':
                r += 1
            moves += 1
        return moves

    results = []
    for test_case in test_cases:
        n, m, board = test_case
        max_moves = 0
        start_r, start_c = 0, 0
        visited = [[False for _ in range(m)] for _ in range(n)]
        for i in range(n):
            for j in range(m):
                for row in visited:
                    row[:] = [False] * m
                moves = move_robot(board, visited, i, j)
                if moves > max_moves:
                    max_moves = moves
                    start_r, start_c = i, j
        results.append((start_r + 1, start_c + 1, max_moves))

    return results

