        m = len(board[0])
        visited = set()
        row, col = start_row, start_col
        moves = 0

        while is_valid_move(row, col, n, m) and (row, col) not in visited:
            visited.add((row, col))
            moves += 1
            new_row, new_col = get_direction(row, col, board[row - 1][col - 1])
            if (new_row, new_col) in visited:
                break
            row, col = new_row, new_col

        return moves

    result = []
    for n, m, board in test_cases:
        max_moves = 0
        best_row, best_col = 1, 1

        for i in range(1, n + 1):
            for j in range(1, m + 1):
                moves = simulate_path(board, i, j)
                if moves > max_moves:
                    max_moves = moves
                    best_row, best_col = i, j

        result.append((best_row, best_col, max_moves))

    return result

