def can_be_fully_erased(s: str) -> str:
    stack = []

    for char in s:
        if stack and stack[-1] != char:
            if stack[-1] == 'A' and char == 'B':
                stack.pop()
            elif stack[-1] == 'B' and char == 'C':
                stack.pop()
            elif stack[-1] == 'A' and char == 'C':
                stack.pop()
            else:
                stack.append(char)
        else:
            stack.append(char)

    return "YES" if not stack else "NO"

