def can_be_fully_erased(s: str) -> str:
    def erase_pairs(s: str) -> str:
        pairs = ['AB', 'BC', 'AC']
        i = 0
        while i < len(s) - 1:
            if s[i:i+2] in pairs:
                s = s[:i] + s[i+2:]
                i = max(0, i - 1)
            else:
                i += 1
        return s

    result = erase_pairs(s)
    return 'YES' if not result else 'NO'

