def find_starting_position(t: int, test_cases: List[Tuple[int, int, str]]) -> List[Tuple[int, int]]:
    def execute_commands(n: int, m: int, commands: str) -> int:
        def is_valid_move(x: int, y: int) -> bool:
            return 1 <= x <= n and 1 <= y <= m

        def move(x: int, y: int, direction: str) -> Tuple[int, int]:
            if direction == 'L':
                return x, y - 1
            elif direction == 'R':
                return x, y + 1
            elif direction == 'U':
                return x - 1, y
            elif direction == 'D':
                return x + 1, y

        max_commands = 0
        max_x, max_y = 0, 0

        for i in range(1, n + 1):
            for j in range(1, m + 1):
                x, y = i, j
                current_commands = 0

                for command in commands:
                    new_x, new_y = move(x, y, command)

                    if is_valid_move(new_x, new_y):
                        x, y = new_x, new_y
                        current_commands += 1
                    else:
                        break

                if current_commands > max_commands:
                    max_commands = current_commands
                    max_x, max_y = i, j

        return max_x, max_y

    result = []

    for case in test_cases:
        n, m, commands = case
        result.append(execute_commands(n, m, commands))

    return result

