        start_r, start_c = 0, 0

        for i in range(n):
            for j in range(m):
                visited = set()
                steps = dfs(board, visited, i, j, 0)
                if steps > max_steps:
                    max_steps = steps
                    start_r, start_c = i, j

        result.append((start_r + 1, start_c + 1, max_steps))

    return result
