        for i in range(n):
            for j in range(m):
                moves = 0
                visited = set()
                x, y = i, j

                while (x, y) not in visited:
                    visited.add((x, y))
                    if board[x][y] == 'L':
                        y -= 1
                    elif board[x][y] == 'R':
                        y += 1
                    elif board[x][y] == 'U':
                        x -= 1
                    elif board[x][y] == 'D':
                        x += 1
                    moves += 1

                    if x < 0 or x >= n or y < 0 or y >= m:
                        break

                max_moves = max(max_moves, moves)

        return max_moves

    result = []
    for n, m, board in test_cases:
        max_moves = count_moves(board)
        result.append((1, 1, max_moves))

    return result

