def find_optimal_starting_position(t: int, test_cases: List[Tuple[int, int, List[str]]) -> List[Tuple[int, int, int]]:
    def calculate_steps(board: List[str], start_row: int, start_col: int) -> int:
        n = len(board)
        m = len(board[0])
        visited = set()
        steps = 0

        while 0 <= start_row < n and 0 <= start_col < m and (start_row, start_col) not in visited:
            visited.add((start_row, start_col))
            direction = board[start_row][start_col]

            if direction == 'L':
                start_col -= 1
            elif direction == 'R':
                start_col += 1
            elif direction == 'U':
                start_row -= 1
            elif direction == 'D':
                start_row += 1

            steps += 1

        return steps

    results = []
    for n, m, board in test_cases:
        max_steps = 0
        optimal_position = (0, 0)

        for i in range(n):
            for j in range(m):
                steps = calculate_steps(board, i, j)
                if steps > max_steps:
                    max_steps = steps
                    optimal_position = (i+1, j+1)

        results.append((optimal_position[0], optimal_position[1], max_steps))

    return results
