        n, m = len(board), len(board[0])
        r, c = pos
        return 0 <= r < n and 0 <= c < m and not visited[r][c]

    def dfs(board: List[str], visited: List[List[bool]], pos: Tuple[int, int], steps: int, memo: List[List[int]]) -> int:
