                if memo[new_r][new_c] == -1:
                    memo[new_r][new_c] = dfs(board, visited, (new_r, new_c), steps + 1, memo)
                max_steps = max(max_steps, memo[new_r][new_c])

        visited[r][c] = False
        return max_steps

    results = []
    for n, m, board in test_cases:
        max_steps = 0
        start_pos = (0, 0)
        for i in range(n):
            for j in range(m):
                visited = [[False for _ in range(m)] for _ in range(n)]
                memo = [[-1 for _ in range(m)] for _ in range(n)]
