                    max_steps = max(max_steps, move(i, j, direction))
        return max_steps

    result = []
    for n, m, board in test_cases:
        max_steps = get_max_steps(board)
        result.append((1, 1, max_steps))  # For simplicity, always start from cell (1, 1)
    return result

