    roles = {}
    max_imposters = 0

    def dfs(node, role):
        if node in roles:
            if roles[node] != role:
                raise ContradictionError
            return
        roles[node] = role
        if role == 'imposter':
            nonlocal max_imposters
            max_imposters += 1

