        """Find the root of the node with path compression."""
        if self.parent[node] != node:
            self.parent[node] = self.find(self.parent[node])
        return self.parent[node]

    def union(self, node1: int, node2: int) -> bool:
        """Union two nodes and return if they were already connected."""
        root1 = self.find(node1)
        root2 = self.find(node2)

        if root1 == root2:
            return False

        if self.rank[root1] > self.rank[root2]:
            self.parent[root2] = root1
        elif self.rank[root1] < self.rank[root2]:
            self.parent[root1] = root2
        else:
            self.parent[root2] = root1
            self.rank[root1] += 1

        return True

    def set_role(self, node: int, role: int) -> bool:
        """Set the role of a node and check for contradictions."""
        root = self.find(node)
        if self.role[root] == -1:
            self.role[root] = role
            return True
        return self.role[root] == role

def max_imposters(t: int, test_cases: List[Tuple[int, int, List[Tuple[int, int, str]]]]) -> List[int]:
    results = []

    for n, m, comments in test_cases:
        if m == 0:
            results.append(n)
            continue

        uf = UnionFind(n + 1)  # +1 because players are 1-indexed

        contradiction = False
        for i, j, c in comments:
            if c == "imposter":
                if not uf.set_role(i, 1) or not uf.set_role(j, 0):
                    contradiction = True
                    break
            elif c == "crewmate":
                if not uf.set_role(i, 0) or not uf.set_role(j, 1):
                    contradiction = True
                    break

        if contradiction:
            results.append(-1)
        else:
            imposters_count = sum(1 for role in uf.role if role == 1)
            results.append(imposters_count)

    return results

# Input interface
