    for i in range(n):
        st[i][0] = euler[i]

    j = 1
    while (1 << j) <= n:
        i = 0
        while (i + (1 << j) - 1) < n:
            if depth[st[i][j - 1]] < depth[st[i + (1 << (j - 1))][j - 1]]:
                st[i][j] = st[i][j - 1]
            else:
                st[i][j] = st[i + (1 << (j - 1))][j - 1]
            i += 1
        j += 1

    return st, log

