    """Query the LCA of nodes u and v."""
    left = first_occurrence[u]
    right = first_occurrence[v]
    if left > right:
        left, right = right, left

    j = log[right - left + 1]
    if depth[st[left][j]] < depth[st[right - (1 << j) + 1][j]]:
        return st[left][j]
    else:
        return st[right - (1 << j) + 1][j]

