class SegmentTree:
    """A Segment Tree to handle updates and queries efficiently."""

    def __init__(self, n: int):
        self.n = n
        self.tree = [0] * (4 * n)
        self.lazy = [0] * (4 * n)

    def update(self, idx: int, start: int, end: int, l: int, r: int, value: int):
        """Update the segment tree with lazy propagation."""
        if self.lazy[idx] != 0:
            self.tree[idx] += self.lazy[idx]
            if start != end:
                self.lazy[2 * idx + 1] += self.lazy[idx]
                self.lazy[2 * idx + 2] += self.lazy[idx]
            self.lazy[idx] = 0

        if start > end or start > r or end < l:
            return

        if start >= l and end <= r:
            self.tree[idx] += value
            if start != end:
                self.lazy[2 * idx + 1] += value
                self.lazy[2 * idx + 2] += value
            return

        mid = (start + end) // 2
        self.update(2 * idx + 1, start, mid, l, r, value)
        self.update(2 * idx + 2, mid + 1, end, l, r, value)
        self.tree[idx] = min(self.tree[2 * idx + 1], self.tree[2 * idx + 2])

    def query(self, idx: int, start: int, end: int, l: int, r: int) -> int:
        """Query the segment tree for the minimum value in a range."""
        if start > end or start > r or end < l:
            return float('inf')

        if self.lazy[idx] != 0:
            self.tree[idx] += self.lazy[idx]
            if start != end:
                self.lazy[2 * idx + 1] += self.lazy[idx]
                self.lazy[2 * idx + 2] += self.lazy[idx]
            self.lazy[idx] = 0

        if start >= l and end <= r:
            return self.tree[idx]

        mid = (start + end) // 2
        left_query = self.query(2 * idx + 1, start, mid, l, r)
        right_query = self.query(2 * idx + 2, mid + 1, end, l, r)
        return min(left_query, right_query)

