class Tree:
    def __init__(self, n: int, a: List[int], edges: List[Tuple[int, int]]):
        self.n = n
        self.a = a
        self.adj = [[] for _ in range(n + 1)]
        for u, v in edges:
            self.adj[u].append(v)
            self.adj[v].append(u)
        self.parent = [-1] * (n + 1)
        self.depth = [0] * (n + 1)
        self.subtree_size = [0] * (n + 1)
        self.chain_head = [-1] * (n + 1)
        self.chain_index = [-1] * (n + 1)
        self.pos_in_base = [-1] * (n + 1)
        self.base_array = []
        self.chain_count = 0
        self.segment_tree = None
        self._decompose_tree()

    def _dfs(self, node: int, parent: int):
        self.parent[node] = parent
        self.subtree_size[node] = 1
        for neighbor in self.adj[node]:
            if neighbor == parent:
                continue
            self.depth[neighbor] = self.depth[node] + 1
            self.subtree_size[node] += self._dfs(neighbor, node)
        return self.subtree_size[node]

    def _hld(self, node: int, parent: int):
        if self.chain_head[self.chain_count] == -1:
            self.chain_head[self.chain_count] = node
        self.chain_index[node] = self.chain_count
        self.pos_in_base[node] = len(self.base_array)
        self.base_array.append(self.a[node - 1])

        special_child = -1
        max_size = -1
        for neighbor in self.adj[node]:
            if neighbor != parent and self.subtree_size[neighbor] > max_size:
                max_size = self.subtree_size[neighbor]
                special_child = neighbor

        if special_child != -1:
            self._hld(special_child, node)

        for neighbor in self.adj[node]:
            if neighbor != parent and neighbor != special_child:
                self.chain_count += 1
                self._hld(neighbor, node)

    def _decompose_tree(self):
        self._dfs(1, -1)
        self._hld(1, -1)
        self.segment_tree = SegmentTree(self.base_array)

    def update_illusion_rate(self, room: int, new_rate: int):
        self.segment_tree.update(self.pos_in_base[room], new_rate)

    def query_energy(self, u: int, v: int) -> int:
