                self.update_segment_tree(2 * index, start, mid, pos, value)
            else:
                self.update_segment_tree(2 * index + 1, mid + 1, end, pos, value)
            self.segment_tree[index] = self.segment_tree[2 * index] + self.segment_tree[2 * index + 1]

    def query_segment_tree(self, index: int, start: int, end: int, left: int, right: int) -> int:
        if right < start or end < left:
            return 0
        if left <= start and end <= right:
            return self.segment_tree[index]
        mid = (start + end) // 2
        left_sum = self.query_segment_tree(2 * index, start, mid, left, right)
        right_sum = self.query_segment_tree(2 * index + 1, mid + 1, end, left, right)
        return left_sum + right_sum

    def update_illusion_rate(self, room: int, new_rate: int):
        self.illusion_rates[room - 1] = new_rate
        self.update_segment_tree(1, 1, self.n, room, new_rate)

    def calculate_min_energy(self, u: int, v: int) -> int:
        # Find LCA of u and v
        lca = self.find_lca(u, v)
        # Calculate energy from u to lca and lca to v
        energy_u_to_lca = self.calculate_energy_path(u, lca)
        energy_lca_to_v = self.calculate_energy_path(lca, v)
        return energy_u_to_lca + energy_lca_to_v

    def find_lca(self, u: int, v: int) -> int:
        if self.depth[u] < self.depth[v]:
            u, v = v, u
        while self.depth[u] > self.depth[v]:
            u = self.parent[u]
        while u != v:
            u = self.parent[u]
            v = self.parent[v]
        return u

    def calculate_energy_path(self, start: int, end: int) -> int:
        energy = 0
        while start != end:
            parent = self.parent[start]
            energy += max(abs(self.illusion_rates[start - 1] + self.illusion_rates[parent - 1]),
                          abs(self.illusion_rates[start - 1] - self.illusion_rates[parent - 1]))
            start = parent
        return energy

