def update_illusion_rate(illusion_rates: List[int], room: int, new_rate: int) -> None:
    """Update the illusion rate of a specified room.

    Args:
        illusion_rates: A list of current illusion rates for each room.
        room: The room number whose illusion rate is to be updated.
        new_rate: The new illusion rate for the specified room.
    """
    illusion_rates[room - 1] = new_rate

def calculate_min_energy(n: int, tunnels: List[Tuple[int, int]], illusion_rates: List[int], start_room: int, end_room: int) -> int:
    """Calculate the minimum sum of energy needed to move from start_room to end_room.

    Args:
        n: The number of rooms in the maze.
        tunnels: A list of tuples representing the tunnels between rooms.
        illusion_rates: A list of current illusion rates for each room.
        start_room: The starting room number.
        end_room: The destination room number.

    Returns:
        The minimum sum of energy required to move from start_room to end_room.
    """
    # Build the adjacency list for the tree
    adjacency_list = defaultdict(list)
    for u, v in tunnels:
        adjacency_list[u].append(v)
        adjacency_list[v].append(u)

    # Perform DFS to find the path and calculate the energy
    def dfs(current: int, target: int, visited: set, current_energy: int) -> int:
        if current == target:
            return current_energy

        visited.add(current)
        min_energy = float('inf')

        for neighbor in adjacency_list[current]:
            if neighbor not in visited:
                energy_cost = max(abs(illusion_rates[current - 1] + illusion_rates[neighbor - 1]),
                                  abs(illusion_rates[current - 1] - illusion_rates[neighbor - 1]))
                total_energy = dfs(neighbor, target, visited, current_energy + energy_cost)
                min_energy = min(min_energy, total_energy)

        visited.remove(current)
        return min_energy

    return dfs(start_room, end_room, set(), 0)

