import heapq

def calculate_sum_of_distances(grid: List[List[int]]) -> int:
    n = len(grid)
    m = len(grid[0])

    # Create a priority queue (min-heap) to store the cells sorted by their distance to the nearest pole
    pq = []

    # Initialize the priority queue with all poles
    for i in range(n):
        for j in range(m):
            if grid[i][j] == 1:
                heapq.heappush(pq, (0, i, j))

    # Directions for moving up, down, left, right
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]

    # Create a distance grid to store the minimum distance to the nearest pole for each cell
    dist = [[float('inf')] * m for _ in range(n)]

    # Process the priority queue
    while pq:
        d, x, y = heapq.heappop(pq)

        # If this cell's distance is already greater than the current minimum, skip it
        if d > dist[x][y]:
            continue

        # Update the distance for the neighboring cells
        for dx, dy in directions:
            nx, ny = x + dx, y + dy
            if 0 <= nx < n and 0 <= ny < m:
                new_dist = d + 1
                if new_dist < dist[nx][ny]:
                    dist[nx][ny] = new_dist
                    heapq.heappush(pq, (new_dist, nx, ny))

    # Calculate the sum of distances
    sum_of_distances = 0
    for i in range(n):
        for j in range(m):
            sum_of_distances += dist[i][j]

    return sum_of_distances

