import numpy as np

def calculate_sum_of_distances(grid: np.ndarray) -> int:
    n, m = grid.shape
    sum_of_distances = 0

    # Precompute the minimum distance to the nearest pole for each cell
    min_distance = np.full((n, m), np.inf)
    queue = []

    # Initialize the queue with all poles
    for x in range(n):
        for y in range(m):
            if grid[x, y] == 1:
                min_distance[x, y] = 0
                queue.append((x, y))

    # Perform BFS to find the minimum distance to the nearest pole
    directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]
    while queue:
        x, y = queue.pop(0)
        for dx, dy in directions:
            nx, ny = x + dx, y + dy
            if 0 <= nx < n and 0 <= ny < m:
                if min_distance[nx, ny] > min_distance[x, y] + 1:
                    min_distance[nx, ny] = min_distance[x, y] + 1
                    queue.append((nx, ny))

    # Calculate the sum of the squares of the distances
    for x in range(n):
        for y in range(m):
            if grid[x, y] == 0:
                sum_of_distances += min_distance[x, y] ** 2

    return sum_of_distances

