    def can_deal_damage(k: int) -> bool:
        prefix_sums = [0] * (n + 1)
        for i in range(n):
            prefix_sums[i + 1] = prefix_sums[i] + max(0, attack_times[i + 1] - attack_times[i])
        for i in range(k, n + 1):
            if prefix_sums[i] - prefix_sums[i - k] >= h:
                return True
        return False

    left, right = 1, n
    while left < right:
        mid = (left + right) // 2
        if can_deal_damage(mid):
            right = mid
        else:
            left = mid + 1
    return left

