def find_min_k(n: int, h: int, attacks: List[int]) -> int:
    attacks.sort(reverse=True)
    max1, max2 = attacks[0], attacks[1]

    # Calculate the minimum k such that the sum of the k largest elements is at least h
    low, high = 1, n
    while low < high:
        mid = (low + high) // 2
        if max1 * mid + max2 * (mid - 1) >= h:
            high = mid
        else:
            low = mid + 1
    return low

