    directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]

    # Initialize the sum of distances
    sum_of_distances = 0

    # Function to check if a cell is within the grid bounds
    def is_valid(x, y):
        return 0 <= x <= n and 0 <= y <= m

    # BFS to find the nearest pole for each point
    def bfs(start_x, start_y):
        visited = [[False] * (m + 1) for _ in range(n + 1)]
        queue = deque([(start_x, start_y, 0)])  # (x, y, distance)
        visited[start_x][start_y] = True

        while queue:
            x, y, dist = queue.popleft()
            if grid[x][y] == 1:
                return dist
            for dx, dy in directions:
                nx, ny = x + dx, y + dy
                if is_valid(nx, ny) and not visited[nx][ny]:
                    visited[nx][ny] = True
                    queue.append((nx, ny, dist + 1))
        return float('inf')  # Should never reach here if there's at least one pole

    # Calculate S(x, y) for each point and sum them up
    for x in range(n + 1):
        for y in range(m + 1):
