def find_smallest_permutation(S: str, T: str) -> str:
    sorted_S = sorted(S)
    remaining_chars = [char for char in sorted_S if char not in T]
    used_chars = [char for char in S if char in T]
    sorted_T = sorted(T)

    result_chars = []
    for char in sorted_T:
        for i, remaining_char in enumerate(remaining_chars):
            if remaining_char > char:
                result_chars.append(remaining_char)
                remaining_chars.pop(i)
                break
        else:
            result_chars.append(remaining_chars.pop(0))

    result_chars.extend(remaining_chars)
    result_chars.extend(used_chars)

    return ''.join(result_chars)

