def calculate_pairs(n, arr):
    # Calculate the mean of the array
    mean_value = sum(arr) / n

    # Initialize a dictionary to count the occurrences of the difference between each element and the mean
    diff_count = {}

    # Count the occurrences of each element
    for num in arr:
        diff = num - mean_value
        if diff in diff_count:
            diff_count[diff] += 1
        else:
            diff_count[diff] = 1

    # Initialize the count of valid pairs
    count = 0

    # Iterate through all pairs of positions [i, j] (i < j)
    for i in range(n):
        for j in range(i + 1, n):
            # Calculate the difference when elements at positions i and j are deleted
            diff_i_j = (arr[i] + arr[j]) - 2 * mean_value

            # Check if the difference exists in the dictionary and is not zero
            if diff_i_j in diff_count and diff_count[diff_i_j] > 0:
                count += 1

    return count

