from itertools import permutations

def is_subsequence(s, t):
    """Check if t is a subsequence of s."""
    it = iter(s)
    return all(char in it for char in t)

def find_lexicographically_smallest_permutation(S: str, T: str) -> str:
    """
    Find the lexicographically smallest permutation of S that does not contain T as a subsequence.

    Args:
    S (str): The input string from which to generate permutations.
    T (str): The target subsequence to avoid in the permutations.

    Returns:
    str: The lexicographically smallest permutation of S that does not contain T as a subsequence.
    """
    for perm in sorted(permutations(S)):
        if not is_subsequence(''.join(perm), T):
            return ''.join(perm)
    return S  # In case no permutation is found that does not contain T, return S itself.

