def find_smallest_permutation(S: str, T: str) -> str:
    """
    Given strings S and T, find the lexicographically smallest permutation of S such that T is not a subsequence of S'.

    Args:
    S (str): A string of lowercase English letters.
    T (str): A string of exactly 3 lowercase English letters which is a permutation of 'abc'.

    Returns:
    str: The lexicographically smallest permutation of S such that T is not a subsequence of S'.
    """
    # Ensure T is a permutation of 'abc'
    if set(T) != {'a', 'b', 'c'}:
        raise ValueError("T must be a permutation of 'abc'")

    # Helper function to check if T is a subsequence of S'
    def is_subsequence(S_prime: str, T: str) -> bool:
        it = iter(S_prime)
        return all(char in it for char in T)

    # Generate all permutations of S and check each one
    from itertools import permutations

    for perm in permutations(S):
        S_prime = ''.join(perm)
        if not is_subsequence(S_prime, T):
            return S_prime

    # If no permutation of S can be found where T is not a subsequence, return an empty string
    return ""

