def find_next_permutation(s):
    s_list = list(s)
    for i in range(len(s_list) - 1, 0, -1):
        if s_list[i - 1] < s_list[i]:
            for j in range(len(s_list) - 1, i - 1, -1):
                if s_list[j] > s_list[i - 1]:
                    s_list[i - 1], s_list[j] = s_list[j], s_list[i - 1]
                    s_list[i:] = s_list[len(s_list) - 1 : i - 1 : -1]
                    return ''.join(s_list)
    return ''.join(sorted(s_list))

def find_smallest_permutation(S: str, T: str) -> str:
    if T == ''.join(sorted(S)):
        return find_next_permutation(S)

    permutations = sorted(set(itertools.permutations(S)))
    for perm in permutations:
        if not is_subsequence(''.join(perm), T):
            return ''.join(perm)

