def read_ints():
    return list(map(int, input().split()))

def longest_increasing_subsequence(arr):
    dp = [1] * len(arr)
    for i in range(len(arr)):
        for j in range(i):
            if arr[i] > arr[j]:
                dp[i] = max(dp[i], dp[j] + 1)
    return max(dp)

def main():
    t = int(input())
    results = []
    for _ in range(t):
        n = int(input())
        arr = read_ints()

        # Try all possible splits
        possible = False
        for i in range(1, n):
            for j in range(i, n):
                subarray1 = arr[:i]
                subarray2 = arr[i:j+1]
                subarray3 = arr[j+1:]

                lis1 = longest_increasing_subsequence(subarray1)
                lis2 = longest_increasing_subsequence(subarray2)
                lis3 = longest_increasing_subsequence(subarray3)

                if (lis1 ^ lis2 ^ lis3) == 0:
                    possible = True
                    break
            if possible:
                break

        results.append("YES" if possible else "NO")

    for result in results:
        print(result)

