        watering_schedule = list(map(int, input().split()))

        height = 1  # The flower starts at 1 centimeter tall.
        consecutive_days_without_watering = 0  # To keep track of consecutive days without watering.

        for i in range(n):
            if watering_schedule[i] == 0:
                consecutive_days_without_watering += 1
                if consecutive_days_without_watering == 2:
                    height = -1  # The flower dies if not watered for two consecutive days.
                    break
            else:
                consecutive_days_without_watering = 0  # Reset the counter if watered.
                if i > 0 and watering_schedule[i-1] == 1:
                    height += 5  # Grow by 5 centimeters if watered the previous day.
                else:
                    height += 1  # Grow by 1 centimeter if not watered the previous day.

        results.append(height)

    for result in results:
        print(result)
