def can_complete_level(grid, n):
    # Initialize a 2D list to keep track of visited cells
    visited = [[False] * n for _ in range(2)]

    # Function to perform DFS
    def dfs(x, y):
        if x < 0 or x >= 2 or y < 0 or y >= n or visited[x][y] or grid[x][y] == '1':
            return False
        if y == n - 1:
            return True
        visited[x][y] = True
        # Move to adjacent cells (up, down, left, right, and diagonals)
        if dfs(x - 1, y + 1) or dfs(x + 1, y + 1) or dfs(x, y + 1):
            return True
        return False

    # Start DFS from the first row
    return dfs(0, 0)

