def simulate_dropping_balls(n, m, k, grid, columns):
    directions = {1: (0, 1), 2: (1, 0), 3: (0, -1)}
    for col in columns:
        row = 0
        current_col = col - 1
        while row < n:
            direction = grid[row][current_col]
            if direction == 1:
                current_col += 1
            elif direction == 2:
                row += 1
            else:
                current_col -= 1
            grid[row][current_col] = 2
        print(current_col + 1, end=' ')

