def simulate_ball_positions(n, m, k, grid, ball_columns):
    directions = {1: (0, 1), 2: (1, 0), 3: (0, -1)}
    for col in ball_columns:
        row = 0
        while row < n:
            direction = grid[row][col - 1]
            if direction == 1:
                col += 1
            elif direction == 2:
                row += 1
            else:
                col -= 1
            grid[row][col - 1] = 2
        print(col, end=' ')

