def simulate_ball_movement(n, m, k, grid, ball_columns):
    def move_right(x, y):
        return x, y + 1

    def move_bottom(x, y):
        return x + 1, y

    def move_left(x, y):
        return x, y - 1

    def simulate_single_ball(x, y):
        while x < n:
            if grid[x][y] == 1:
                x, y = move_right(x, y)
            elif grid[x][y] == 2:
                x, y = move_bottom(x, y)
            else:
                x, y = move_left(x, y)
            if y == 0 or y == m - 1:
                break
        return y

    result = []
    for col in ball_columns:
        result.append(simulate_single_ball(0, col - 1))

    return result

