def simulate_robot_movement(n, m, commands):
    directions = {'L': (0, -1), 'R': (0, 1), 'D': (1, 0), 'U': (-1, 0)}

    def is_valid_move(x, y):
        return 1 <= x <= n and 1 <= y <= m

    def execute_commands(start_x, start_y):
        x, y = start_x, start_y
        successful_moves = 0
        for command in commands:
            dx, dy = directions[command]
            new_x, new_y = x + dx, y + dy
            if is_valid_move(new_x, new_y):
                x, y = new_x, new_y
                successful_moves += 1
            else:
                break
        return successful_moves, (x, y)

    max_successful_moves = 0
    optimal_starting_cell = (1, 1)

    for i in range(1, n + 1):
        for j in range(1, m + 1):
            moves, _ = execute_commands(i, j)
            if moves > max_successful_moves:
                max_successful_moves = moves
                optimal_starting_cell = (i, j)

    return optimal_starting_cell

