def simulate_robot_movement(n, m, start_row, start_col, commands):
    row, col = start_row, start_col
    successful_moves = 0

    for command in commands:
        if command == 'L' and col > 1:
            col -= 1
            successful_moves += 1
        elif command == 'R' and col < m:
            col += 1
            successful_moves += 1
        elif command == 'U' and row > 1:
            row -= 1
            successful_moves += 1
        elif command == 'D' and row < n:
            row += 1
            successful_moves += 1
        else:
            break

    return successful_moves

