def simulate_robot_movement(n, m, commands):
    def is_valid_move(x, y):
        return 1 <= x <= n and 1 <= y <= m

    def move(x, y, command):
        if command == 'L':
            return x, y - 1
        elif command == 'R':
            return x, y + 1
        elif command == 'U':
            return x - 1, y
        elif command == 'D':
            return x + 1, y

    def count_successful_moves(start_x, start_y):
        x, y = start_x, start_y
        successful_moves = 0
        for command in commands:
            new_x, new_y = move(x, y, command)
            if is_valid_move(new_x, new_y):
                successful_moves += 1
                x, y = new_x, new_y
            else:
                break
        return successful_moves

    max_successful_moves = 0
    best_starting_cell = (1, 1)

    for i in range(1, n + 1):
        for j in range(1, m + 1):
            successful_moves = count_successful_moves(i, j)
            if successful_moves > max_successful_moves:
                max_successful_moves = successful_moves
                best_starting_cell = (i, j)

    return best_starting_cell

