def calculate_successful_moves(n, m, s):
    def is_valid_move(x, y):
        return 1 <= x <= n and 1 <= y <= m

    def move(x, y, direction):
        if direction == 'L':
            return x, y - 1
        elif direction == 'R':
            return x, y + 1
        elif direction == 'U':
            return x - 1, y
        elif direction == 'D':
            return x + 1, y

    def count_successful_moves(start_x, start_y):
        x, y = start_x, start_y
        successful_moves = 0
