def cyclic_shift_and_operation(t, test_cases):
    def cyclic_shift(a, d):
        return a[-d:] + a[:-d]

    def logical_and(a, b):
        return [x & y for x, y in zip(a, b)]

    def check_zeros(a):
        return all(x == 0 for x in a)

    results = []
    for i in range(t):
        n, d = test_cases[i][0], test_cases[i][1]
        a = test_cases[i][2]

        steps = 0
        seen = {tuple(a): 0}
        while True:
            a_shifted = cyclic_shift(a, d)
            a = logical_and(a, a_shifted)
            steps += 1

            if check_zeros(a):
                results.append(steps)
                break
            if tuple(a) in seen:
                results.append(-1)
                break
            seen[tuple(a)] = steps

    return results

