        if board[x][y] == 'L':
            return x, y - 1
        elif board[x][y] == 'R':
            return x, y + 1
        elif board[x][y] == 'U':
            return x - 1, y
        elif board[x][y] == 'D':
            return x + 1, y

    def calculate_moves(start_x, start_y):
        visited = set()
        x, y = start_x, start_y
        moves = 0

        while is_valid_move(x, y) and (x, y) not in visited:
            visited.add((x, y))
            moves += 1
            x, y = get_next_move(x, y)

        return moves

