                    successful_moves += 1
                else:
                    break
            return successful_moves

        max_successful_moves = 0
        optimal_starting_cell = (1, 1)

        for i in range(1, n + 1):
            for j in range(1, m + 1):
                successful_moves = simulate_move(i, j)
                if successful_moves > max_successful_moves:
