        directions = {'L': (0, -1), 'R': (0, 1), 'D': (1, 0), 'U': (-1, 0)}
        successful_moves = 0
        current_row, current_col = start_row, start_col

        for command in commands:
            new_row = current_row + directions[command][0]
            new_col = current_col + directions[command][1]

            if 1 <= new_row <= n and 1 <= new_col <= m:
                successful_moves += 1
                current_row, current_col = new_row, new_col
            else:
                break

        return successful_moves

    for i in range(t):
        n, m = test_cases[i][0]
        commands = test_cases[i][1]

        max_successful_moves = 0
        optimal_starting_cell = (1, 1)

        for row in range(1, n + 1):
            for col in range(1, m + 1):
                successful_moves = count_successful_moves(n, m, commands, row, col)

                if successful_moves > max_successful_moves:
                    max_successful_moves = successful_moves
                    optimal_starting_cell = (row, col)

        print(f"{optimal_starting_cell[0]} {optimal_starting_cell[1]}")

