def calculate_max_commands(n, m, commands):
    directions = {'L': (0, -1), 'R': (0, 1), 'D': (1, 0), 'U': (-1, 0)}

    def is_valid_move(x, y):
        return 0 <= x < n and 0 <= y < m

    def execute_commands(start_x, start_y):
        x, y = start_x, start_y
        count = 0
        visited = set()

        while is_valid_move(x, y) and (x, y) not in visited:
            visited.add((x, y))
            count += 1
            dx, dy = directions[commands[count % len(commands)]]
            x, y = x + dx, y + dy

        return count

    max_commands = 0
    best_cell = (1, 1)

    for i in range(n):
        for j in range(m):
            commands_executed = execute_commands(i, j)
            if commands_executed > max_commands:
                max_commands = commands_executed
                best_cell = (i+1, j+1)

    return best_cell

