def balance_substrings(t, test_cases):
    def count_substrings(s, sub):
        count = 0
        for i in range(len(s) - len(sub) + 1):
            if s[i:i + len(sub)] == sub:
                count += 1
        return count

    def balance_string(s):
        ab_count = count_substrings(s, 'ab')
        ba_count = count_substrings(s, 'ba')

        if ab_count == ba_count:
            return s

        if ab_count > ba_count:
            return s.replace('a', 'b', ab_count - ba_count)
        else:
            return s.replace('b', 'a', ba_count - ab_count)

    for i in range(t):
        result = balance_string(test_cases[i])
        print(result)

