def execute_commands(t, test_cases):
    def count_successful_commands(n, m, s):
        directions = {'L': (0, -1), 'R': (0, 1), 'D': (1, 0), 'U': (-1, 0)}

        def is_valid_move(x, y):
            return 0 <= x < n and 0 <= y < m

        def simulate_movement(start_x, start_y):
            x, y = start_x, start_y
            successful_commands = 0
            for command in s:
                dx, dy = directions[command]
                new_x, new_y = x + dx, y + dy
                if is_valid_move(new_x, new_y):
                    x, y = new_x, new_y
                    successful_commands += 1
                else:
                    break
            return successful_commands

        max_successful_commands = 0
        best_starting_position = (1, 1)

        for i in range(n):
            for j in range(m):
                successful_commands = simulate_movement(i, j)
                if successful_commands > max_successful_commands:
                    max_successful_commands = successful_commands
                    best_starting_position = (i+1, j+1)

        return best_starting_position

    results = []
    for i in range(t):
        n, m = test_cases[i][0]
        s = test_cases[i][1]
        result = count_successful_commands(n, m, s)
        results.append(result)

    return results

