def max_commands_possible(t, test_cases):
    def count_commands_executed(n, m, s):
        directions = {'L': (0, -1), 'R': (0, 1), 'U': (-1, 0), 'D': (1, 0)}

        def is_valid_move(x, y):
            return 0 <= x < n and 0 <= y < m

        def simulate_movement(start_x, start_y):
            x, y = start_x, start_y
            commands_executed = 0

            for command in s:
                dx, dy = directions[command]
                new_x, new_y = x + dx, y + dy

                if is_valid_move(new_x, new_y):
                    x, y = new_x, new_y
                    commands_executed += 1
                else:
                    break

            return commands_executed

        max_commands = 0
        max_cell = (1, 1)

        for i in range(n):
            for j in range(m):
                commands_executed = simulate_movement(i, j)
                if commands_executed > max_commands:
                    max_commands = commands_executed
                    max_cell = (i + 1, j + 1)

        return max_cell

    results = []
    for i in range(t):
        n, m = test_cases[i][0]
        s = test_cases[i][1]
        result = count_commands_executed(n, m, s)
        results.append(result)

    return results

