def simulate_robot_movement(n, m, commands):
    def is_valid_move(x, y):
        return 0 <= x < n and 0 <= y < m

    def execute_commands(start_x, start_y):
        x, y = start_x, start_y
        executed_commands = 0
        for command in commands:
            if command == 'L':
                y -= 1
            elif command == 'R':
                y += 1
            elif command == 'U':
                x -= 1
            elif command == 'D':
                x += 1

            if not is_valid_move(x, y):
                break
            executed_commands += 1

        return executed_commands

    max_executed_commands = 0
    best_starting_cell = (1, 1)

    for i in range(n):
        for j in range(m):
            executed_commands = execute_commands(i, j)
            if executed_commands > max_executed_commands:
                max_executed_commands = executed_commands
                best_starting_cell = (i+1, j+1)

    return best_starting_cell

